import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

const TetDecorations = ({ enabled }) => {
  if (!enabled) return null;

  const flowerVariants = {
    initial: { y: -100, opacity: 0, rotate: 0 },
    animate: { 
      y: 700,
      opacity: [0, 1, 1, 0],
      rotate: 360,
      transition: { 
        duration: 5,
        repeat: Infinity,
        ease: "linear"
      }
    }
  };

  const fireworkVariants = {
    initial: { scale: 0, opacity: 0 },
    animate: {
      scale: [0, 1.2, 0],
      opacity: [0, 1, 0],
      transition: {
        duration: 1.5,
        repeat: Infinity,
        repeatDelay: 3
      }
    }
  };

  return (
    <>
      {/* Overlay gradient */}
      <div className="absolute inset-0 bg-gradient-to-r from-red-600/20 to-yellow-500/20"></div>

      {/* Animated flowers */}
      {[...Array(10)].map((_, i) => (
        <motion.div
          key={i}
          className="absolute"
          style={{
            left: `${Math.random() * 100}%`,
            top: -20,
            zIndex: 20
          }}
          variants={flowerVariants}
          initial="initial"
          animate="animate"
          custom={i}
        >
          <span className="text-2xl">{Math.random() > 0.5 ? '🌸' : '🌺'}</span>
        </motion.div>
      ))}

      {/* Fireworks */}
      {[...Array(5)].map((_, i) => (
        <motion.div
          key={i}
          className="absolute"
          style={{
            left: `${20 + Math.random() * 60}%`,
            top: `${20 + Math.random() * 40}%`,
            zIndex: 20
          }}
          variants={fireworkVariants}
          initial="initial"
          animate="animate"
          custom={i}
        >
          <span className="text-4xl">✨</span>
        </motion.div>
      ))}

      {/* Corner decorations */}
      <motion.div
        animate={{ y: [0, -10, 0] }}
        transition={{ duration: 2, repeat: Infinity }}
        className="absolute top-20 left-20 z-20"
      >
        <span className="text-6xl">🏮</span>
      </motion.div>
      <motion.div
        animate={{ y: [0, 10, 0] }}
        transition={{ duration: 2.5, repeat: Infinity }}
        className="absolute top-20 right-20 z-20"
      >
        <span className="text-6xl">🏮</span>
      </motion.div>
      <div className="absolute bottom-0 left-0 text-8xl z-20">
        <motion.div
          animate={{ rotate: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          🎋
        </motion.div>
      </div>
      <div className="absolute bottom-0 right-0 text-8xl z-20">
        <motion.div
          animate={{ rotate: [0, -10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          🎋
        </motion.div>
      </div>
    </>
  );
};

const Hero = () => {
  const navigate = useNavigate();
  // Có thể thay đổi giá trị này để bật/tắt hiệu ứng Tết
  const [showTetEffects] = useState(true);

  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: true,
    });
  }, []);

  return (
    <div className="relative bg-gradient-to-r from-blue-600 to-blue-800 h-screen overflow-hidden">
      <TetDecorations enabled={showTetEffects} />

      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="pt-32 pb-12 md:pt-40 md:pb-20">
          <div className="text-center">
            <motion.h1
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="text-4xl tracking-tight font-extrabold text-white sm:text-5xl md:text-6xl"
            >
              <motion.span 
                className="block"
                animate={showTetEffects ? { scale: [1, 1.02, 1] } : {}}
                transition={{ duration: 2, repeat: Infinity }}
              >
                {showTetEffects ? 'Chúc Mừng Năm Mới 2024' : 'Phuc An Solutions'}
              </motion.span>
              <span className={`block ${showTetEffects ? 'text-yellow-300' : 'text-blue-200'} mt-3`}>
                Phuc An Solutions
              </span>
            </motion.h1>

            <div data-aos="fade-up" className="mt-8">
              <p className="mt-3 max-w-md mx-auto text-lg text-white sm:text-xl md:mt-5 md:max-w-3xl">
                Cung cấp giải pháp toàn diện về CNTT, xây dựng hệ thống mạng, 
                camera an ninh và các dịch vụ công nghệ hiện đại.
              </p>
            </div>

            <div data-aos="fade-up" data-aos-delay="200" className="mt-10 space-x-4">
              <motion.button 
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => navigate('/contact')}
                className={`${showTetEffects ? 'bg-red-600 hover:bg-red-700' : 'bg-white text-blue-600 hover:bg-blue-50'} px-8 py-3 rounded-full font-bold transition duration-300 shadow-lg text-white`}
              >
                Liên Hệ Ngay
              </motion.button>
              <motion.button 
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => navigate('/services')}
                className={`${showTetEffects ? 'bg-yellow-500 hover:bg-yellow-600' : 'bg-white text-blue-600 hover:bg-blue-50'} px-8 py-3 rounded-full font-bold transition duration-300 shadow-lg ${showTetEffects ? 'text-white' : ''}`}
              >
                Tìm Hiểu Thêm
              </motion.button>
            </div>
          </div>
        </div>
      </div>

      {/* Background decoration */}
      <div className="absolute bottom-0 w-full">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
          <path fill="#ffffff" fillOpacity="1" d="M0,96L48,112C96,128,192,160,288,160C384,160,480,128,576,112C672,96,768,96,864,112C960,128,1056,160,1152,160C1248,160,1344,128,1392,112L1440,96L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
        </svg>
      </div>
    </div>
  );
};

export default Hero; 
// export default Hero;
