import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Hero = () => {
  const navigate = useNavigate();

  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: true,
    });
  }, []);

  return (
    <div className="relative h-screen">
      {/* Background image with overlay */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{
          backgroundImage: "url('/images/tet-background.jpg')", // Hình nền Tết
        }}
      >
        {/* Overlay gradient để text dễ đọc */}
        <div className="absolute inset-0 bg-gradient-to-r from-black/50 to-black/30"></div>
      </div>

      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="pt-32 pb-12 md:pt-40 md:pb-20">
          <div className="text-center">
            <motion.h1
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="text-4xl tracking-tight font-extrabold text-white sm:text-5xl md:text-6xl"
            >
              <span className="block">Phuc An Solutions</span>
              <span className="block text-yellow-300">Cho Doanh Nghiệp Của Bạn</span>
            </motion.h1>

            <div data-aos="fade-up" className="mt-8">
              <p className="mt-3 max-w-md mx-auto text-lg text-gray-200 sm:text-xl md:mt-5 md:max-w-3xl">
                Cung cấp giải pháp toàn diện về CNTT, xây dựng hệ thống mạng, 
                camera an ninh và các dịch vụ công nghệ hiện đại.
              </p>
            </div>

            <div data-aos="fade-up" data-aos-delay="200" className="mt-10 space-x-4">
              <button 
                onClick={() => navigate('/contact')}
                className="bg-yellow-500 text-white px-8 py-3 rounded-full font-bold hover:bg-yellow-600 transition duration-300 shadow-lg"
              >
                Liên Hệ Ngay
              </button>
              <button 
                onClick={() => navigate('/services')}
                className="bg-white/90 text-gray-900 px-8 py-3 rounded-full font-bold hover:bg-white transition duration-300 shadow-lg"
              >
                Tìm Hiểu Thêm
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Wave decoration */}
      <div className="absolute bottom-0 w-full">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
          <path fill="#ffffff" fillOpacity="1" d="M0,96L48,112C96,128,192,160,288,160C384,160,480,128,576,112C672,96,768,96,864,112C960,128,1056,160,1152,160C1248,160,1344,128,1392,112L1440,96L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
        </svg>
      </div>
    </div>
  );
};

export default Hero; 
// export default Hero;
