import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Services from './components/Services';
import Solutions from './components/Solutions';
import Contact from './components/Contact';
import Footer from './components/Footer';
import SolutionDetail from './components/SolutionDetail';
import { solutions } from './data/solutions';
import ServiceDetail from './components/ServiceDetail';
import ErrorBoundary from './components/ErrorBoundary';
import ScrollToTop from './components/ScrollToTop';
import Blog from './components/Blog';
import BlogDetail from './components/BlogDetail';
import { motion } from 'framer-motion';

const TetOverlay = () => (
  <div className="fixed inset-0 pointer-events-none z-50">
    {[...Array(15)].map((_, i) => (
      <motion.div
        key={i}
        initial={{ 
          x: Math.random() * window.innerWidth,
          y: -50,
          rotate: 0 
        }}
        animate={{ 
          y: window.innerHeight + 50,
          rotate: 360,
          opacity: [0, 1, 1, 0]
        }}
        transition={{
          duration: Math.random() * 3 + 2,
          repeat: Infinity,
          delay: Math.random() * 2
        }}
        className="absolute text-2xl"
      >
        {Math.random() > 0.5 ? 'ðŸŒ¸' : 'ðŸŒº'}
      </motion.div>
    ))}
  </div>
);

function App() {
  return (
    <Router>
      <ErrorBoundary>
        <ScrollToTop />
        <TetOverlay />
        <div className="min-h-screen">
          <Navbar />
          <Routes>
            <Route path="/" element={
              <>
                <Hero />
                <Services />
                <Solutions />
              </>
            } />
            <Route path="/services" element={<Services />} />
            <Route path="/services/:id" element={<ServiceDetail />} />
            <Route path="/solutions" element={<Solutions />} />
            <Route path="/solutions/network" element={<SolutionDetail solution={solutions.network} />} />
            <Route path="/solutions/security" element={<SolutionDetail solution={solutions.security} />} />
            <Route path="/solutions/infrastructure" element={<SolutionDetail solution={solutions.infrastructure} />} />
            <Route path="/solutions/construction" element={<SolutionDetail solution={solutions.construction} />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/blog/:id" element={<BlogDetail />} />
          </Routes>
          <Footer />
        </div>
      </ErrorBoundary>
    </Router>
  );
}

export default App;
