import React from 'react';
import { motion } from 'framer-motion';

const services = [
  {
    title: 'Giải Pháp Mạng',
    description: 'Thiết kế và triển khai hệ thống mạng doanh nghiệp, tối ưu hóa hiệu suất và bảo mật.',
    icon: '🌐',
  },
  {
    title: 'Camera An Ninh',
    description: 'Hệ thống camera giám sát thông minh, tích hợp AI để bảo vệ tài sản của bạn.',
    icon: '📹',
  },
  {
    title: 'Hạ Tầng CNTT',
    description: 'Xây dựng và quản lý hạ tầng CNTT toàn diện cho doanh nghiệp.',
    icon: '🏢',
  },
  {
    title: 'Cloud Solutions',
    description: 'Giải pháp đám mây hiện đại, đảm bảo khả năng mở rộng và linh hoạt.',
    icon: '☁️',
  }
];

const Services = () => {
  return (
    <section className="py-20 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h2 className="text-3xl font-extrabold text-gray-900 sm:text-4xl">
            Dịch Vụ Của Chúng Tôi
          </h2>
          <p className="mt-4 text-xl text-gray-600">
            Cung cấp đầy đủ các giải pháp công nghệ cho doanh nghiệp của bạn
          </p>
        </div>

        <div className="mt-20 grid grid-cols-1 gap-8 md:grid-cols-2 lg:grid-cols-4">
          {services.map((service, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true }}
              className="service-card group"
            >
              <div className="text-4xl mb-4">{service.icon}</div>
              <h3 className="text-xl font-semibold text-gray-900 group-hover:text-blue-600">
                {service.title}
              </h3>
              <p className="mt-3 text-gray-500">
                {service.description}
              </p>
              <div className="mt-4">
                <a href="#" className="text-blue-600 hover:text-blue-700 font-medium">
                  Tìm hiểu thêm →
                </a>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
